import axios from "axios";
import { useState } from 'react';
import './App.css';
import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';
import SearchIcon from '@mui/icons-material/Search';

function App() {

  const [songTitle, setSongTitle] = useState("");
  const [songArtist, setSongArtist] = useState("");
  const [songLyrics, setLyrics] = useState<undefined | { lyrics: "" }>(undefined);


  const LYRIC_BASE_URL = "https://api.lyrics.ovh/v1";

  return (
    <div>
      <h1>
        Find Lyrics
      </h1>

      <div>
      <TextField
          id="search-bar"
          className="text"
          value={songArtist}
          onChange={(prop: any) => {
            setSongArtist(prop.target.value);
          }}
          label="Enter Artist"
          variant="outlined"
          placeholder="Search..."
          size="small"
        />


        <label>Title: </label>
        <input
          type="text"
          id="title"
          name="title"
          onChange={e => setSongTitle(e.target.value)}
        />
        <br />
        <label>Artist: </label>
        <input type="text" id="artist" name="artist" onChange={e => setSongArtist(e.target.value)} /><br />
        <Button
        onClick={() => {
          search();
        }} 
        variant="outlined" 
        startIcon={<SearchIcon />}>
        Search
      </Button>

      </div>


      <div id="result"></div>

    </div>


  );

  function search() {
    axios
      .get(LYRIC_BASE_URL + "/" + songArtist + "/" + songTitle)
      .then((res) => {
        setLyrics(res.data);
      })
      .catch((err) => {
        console.log("Lyric not found");
        setLyrics(undefined);
      });

    songLyrics === undefined ? (
      document.getElementById("result")!.innerHTML = "No Song Found. You have entered| Artist: " + songArtist + " | Title: " + songTitle

    ) : (
      document.getElementById("result")!.innerHTML = JSON.stringify(songLyrics.lyrics).replaceAll("\\n", "<br>").replaceAll("\\r", "<br>")
    )
  }
}

export default App;
